-- Get Total Farms count
SELECT COUNT(*) as total_farms FROM farms;

-- Get Farm Area sum (in hectares)
SELECT COALESCE(SUM(CAST(sizeHectares AS DECIMAL(10,2))), 0) as total_area_ha FROM farms;

-- Get Active Crops count
SELECT COUNT(*) as active_crops FROM cropCycles WHERE status = 'active';

-- Get Pending Tasks count
SELECT COUNT(*) as pending_tasks FROM fieldWorkerTasks WHERE status = 'pending';

-- Get Weather Alerts count
SELECT COUNT(*) as weather_alerts FROM weatherHistory WHERE eventType IN ('alert', 'warning', 'severe');

-- Get Livestock count
SELECT COUNT(*) as total_livestock FROM animals;

